class Solution {
public:

    const int M = 1e9+7;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        // memset(dp,-1,sizeof(dp));
        vector<vector<long long>>arr(n+1,vector<long long>(401,0));
        sort(requirements.begin(), requirements.end());
        long long nn = requirements.size();
        long long pointer = 0;
        arr[0][0] = 1;
        for(int i=0; i<n; i++){
            if(pointer!=nn && i==requirements[pointer][0]){
                long long end = requirements[pointer][0];
                long long cnt = requirements[pointer][1];
                for(int j=cnt; j>=0; j--){
                    if(j<=(cnt-(end+1))){
                        break;
                    }
                    arr[end+1][cnt] += arr[end][j];
                    arr[end+1][cnt] %= M;
                }
                pointer++;
            }
            else{
                long long end = i;
                for(int cnt=0; cnt<=400; cnt++){
                    for(int j=cnt; j>=0; j--){
                        if(j<=(cnt-(end+1))){
                            break;
                        }
                        arr[end+1][cnt] += arr[end][j];
                        arr[end+1][cnt] %= M;
                    }
                }
            }
        }
        long long ans = 0;
        for(int i=0; i<=400; i++){
            ans += arr[n][i];
            ans %= M;
        }
        // cout << arr[1][0] << "\n";
        // cout << arr[1][1] << "\n";
        // cout << arr[1][2] << "\n";
        return ans;
    }
};
