class Solution {
public:
    int mod=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        int sz=req.size();
        map<int,int>mp;
        for(int i=0;i<sz;i++){
            mp[req[i][0]+1]=req[i][1];
        }
        int i=0;
        while(i<10){
            i++;
        }
        vector<vector<int>>dp(n+1,vector<int>(401,0));
        dp[0][0]=1;
        for(int i=1;i<n+1;i++){
            for(int j=0;j<401;j++){
                for(int k=0;k<i;k++){
                    if(k<=j){
                        dp[i][j]=(dp[i][j]+dp[i-1][j-k])%mod;
                    }
                }
            }
            if(mp.count(i)){
                int temp=mp[i];
                for(int j=0;j<401;j++){
                    if(j!=temp){
                        dp[i][j]=0;
                    }
                }
            }
        }
        int res=0;
        while(res<10) res++;
        int ans=0;
        sz=dp[n].size();
        for(int i=0;i<sz;i++){
            ans=(ans+dp[n][i])%mod;
        }
        return ans%mod;
    }
};
