class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& arr) {
        int m = arr.size();
        sort(arr.begin(),arr.end());
        for(int i=1;i<m-1;i++){
            if(arr[i][1]<arr[i-1][1]){
                return 0;
            }
        }

        int p=arr[m-1][1];
        int mod=1e9+7;
        vector<vector<int>> dp(n,vector<int>(p+1,0));
        dp[0][0]=1;

        int pre=0,ind = 0;
        if(arr[0][0]==0){
            if(arr[0][1]!=0){
                return 0;
            }
            ind++;
        }
        // cout<<arr[m-1][0];
        // return 1;
        for(int i=1;i<n;i++){
            if(ind == m){
                break;
            }
            for(int j=arr[ind][1];j>=pre;j--){
                int x=arr[ind][1]-pre;
                long long sum=0;
                // cout<<x<<"  ";
                for(int k=0;k<=x;k++){
                    // cout<<i<<' '<<j<<' '<<k<<endl;
                    if(k>i){
                        break;
                    }
                    if(j-k<0){
                        break;
                    }
                    sum+=dp[i-1][j-k];
                }
                dp[i][j]=sum%mod;
                if(i==arr[ind][0]){
                    pre=arr[ind][1];
                    ind++;
                    // cout<<ind<<' '<<i<<j;
                    break;
                }
            }
            // cout<<2<<' ';
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<=p;j++){
        //         cout<<dp[i][j]<<' ';
        //     }
        //     cout<<endl;
        // }
        // return 1;
        return dp[n-1][pre];
    }
};
