class Solution {
public:
    #define ll long long
    const ll mod=1e9+7;
    ll dp[301][401];

    ll f(ll n,ll k,unordered_map<ll,ll>mp){
        if(k<0)return 0;
        if(n==0){
            if(k==0)return 1;
            else return 0;

        }
        if(dp[n][k]!=-1)return dp[n][k];

        ll c=0;

        for(ll i=0;i<n;i++){
            c=(c+f(n-1,k-i,mp)%mod)%mod;
        }

        if(mp.find(n)!=mp.end()){
            ll x=mp[n];
            if(k!=x)c=0;
        }
        return dp[n][k]=c;
    }
    int numberOfPermutations(int n, vector<vector<int>>& a) {
        ll m=a.size();
        ll ans=0;
        unordered_map<ll,ll>mp;
        vector<vector<ll>>dp1(n+1,vector<ll>(401ll,0));
        //memset(dp,-1,sizeof(dp));
        for(int i=0;i<m;i++){
            ll idx=a[i][0];
            ll c=a[i][1];
            mp[idx+1]=c;
        }



        // for(ll k=0;k<=400;k++){
        //     ans=(ans+f((ll)n,k,mp)%mod)%mod;
        // }
        dp1[0][0]=1;
        for(ll i=0;i<=n;i++){
            for(ll j=0;j<=400;j++){
                for(ll k=0;k<i;k++){

                    if(j-k>=0){
                        dp1[i][j]=(dp1[i][j]+dp1[i-1][j-k])%mod;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                ll x=mp[i];
                for(ll l=0;l<=400;l++){
                    if(l!=x)dp1[i][l]=0;
                }
            }
        }
        for(auto x:dp1[n]){
            ans=(ans+x)%mod;
        }
        return (int)ans;
    }
};
