#define ll long long
#define it(i, j, n) for (int i=j; i<n; i++)
class Solution {
public:
    const ll M = 1e9+7 ;
    const int mx = 400;
    int numberOfPermutations(int n, vector<vector<int>>& a) {
        ll ans = 0;
        vector<vector<ll>> prm(n + 1, vector<ll>(mx + 1, 0));
        map<ll, ll> mp;
        prm[0][0] = 1;
        for (auto& x : a) {
            mp[x[0] + 1] = x[1];
        }

        it (i, 1, n+1) {
            it (j, 0, mx+1) {
                it (x, 0, i) {
                    if (j >= x)    prm[i][j] = (prm[i - 1][j-x] + prm[i][j]) % M;
                }
            }

            if (mp.find(i) != mp.end()) {
                it (j, 0, mx+1) {
                    if (j != mp[i])   prm[i][j] = 0;
                }
            }
        }

        for (auto &x : prm[n]) {
            ans = (ans + x) % M;
        }

        return ans;
    }
};
