class Solution {
    int mod=1000000007;
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<int>> dp(301,vector<int>(401,0));
        int k=400;
        dp[0][0] = 1; 
        map<int,int> mp;
        for(auto aa:requirements){
            mp[aa[0]+1]=aa[1];
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= k; j++) {
                dp[i][j] = 0;
                for (int m = 0; m <= min(j, i - 1); m++) {
                    dp[i][j] =(dp[i][j]+ dp[i-1][j-m])%mod;
               }
            }
            if(mp.count(i)){
                for(int j=0;j<=k;j++){
                    if(j!=mp[i]) dp[i][j]=0;
                }
            }
        }
        long long count=0;
        for(int i=0;i<=k;i++){
            count=(count+dp[n][i])%mod;
        }
        return count;
    }
};
