#define mod 1000000007
long long dp[301][401];
class Solution {
public:

    int find(int n, vector<vector<int>>& requirements)
    {
        memset(dp, 0, sizeof(dp));

        dp[0][0] = 1;

unordered_map<int,int> mp;
        for(auto it : requirements)
        {
            mp[it[0] + 1] = it[1];
        }
        for(int i=1;i<=n;i++)
        {
            for(int j=0;j<401;j++)
            {
                for(int k=0;k<i;k++)
                {
                    if((j - k) >= 0)
                    {
                        dp[i][j] = (dp[i][j] + dp[i-1][j - k]) % mod;
                    }
                }
            }

            if(mp.count(i))
            {
                for(int j=0;j<401;j++)
                {
                    if(j != mp[i])
                    {
                        dp[i][j] = 0;
                    }
                }
            }
        }

        long long ans = 0;

        for(auto it : dp[n])
        {
            ans = (ans + it) % mod;
        }

        return ans;
    }

    int numberOfPermutations(int n, vector<vector<int>>& requirements) 
    {
        return find(n, requirements);
    }
};
