class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
unordered_map<long long int, long long int> map1;
for(int i=0;i<requirements.size();i++){
    map1[requirements[i][0]+1]=requirements[i][1];
}
vector<vector<long long int>> sum(n + 1, vector<long long int>(400 + 1, 0));
sum[0][0] = 1;

for(int xyz=1;xyz<=n;xyz++)
{
    for(int abcd=0;abcd <= 400;abcd++)
    {
        for (int p=0;p < xyz;p++)
        {
            int pqrs = abcd - p;
            if (pqrs >= 0)
            {
                sum[xyz][abcd] += (
                                  sum[xyz - 1][pqrs]) %
                                 1000000007;
            }
        }
    }
    if (map1.count(xyz) > 0)
    {
        int is = map1[xyz];
        for (int o=0;o <= 400;o++)
        {
            if (o != is)
            {
                sum[xyz][o] = 0;
            }
        }
    }
}

long long res = 0;
for(int u=0;u <= 400;u++)
{
    res += (sum[n][u]) % 1000000007;
}
return (int)res;

    }
};
