class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) 
    {
        long long mod=1e9+7;
        map<long long,long long>a;
        for(auto v:requirements)
        {
            a[v[0]+1]=v[1];
        }
        long long dp[401][401]={0};
        dp[0][0]=1;
        for(long long i=1;i<=n;i++)
        {
            for(long long j=0;j<=400;j++)
            {
                for(long long k=0;k<i;k++)
                {
                    if((j-k)>=0)
                    {
                        dp[i][j]+=((dp[i-1][(j-k)])%mod);
                        dp[i][j]%=mod;
                    }
                }
            }
            if(a.find(i)!=a.end())
            {
                long long tmp=dp[i][a[i]];
                memset(dp[i],0,sizeof(dp[i]));
                dp[i][a[i]]=tmp;
            }
        }
        long long ans=0;
        for(long long i=0;i<401;i++)
        {
            ans+=dp[n][i];
            ans%=mod;
        }
        return ans;

    }
};
