class Solution {
public:

    unordered_map<int,int> m;
       int ans = 0;
    const int z = 1000000007;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        vector<vector<int>> arr(n+1,vector<int>(401,0));
        for(int i=0;i<req.size();i++) m[req[i][0]+1]=req[i][1];
        arr[0][0] = 1;
        for (int i=1;i<=n;i++) {
            for (int j=0;j<=400;j++) {
              for (int k=0;k<i;k++) {
                  if (j-k>=0) arr[i][j]=(arr[i][j]+arr[i-1][j-k])%z;
              }
            }
            if (m.find(i)!=m.end()){
                int p=m[i];
                for (int a=0;a<=400;a++) {
                    if (a!=p) {
                    arr[i][a]=0;
                }
            }
        }
    }

    for (int i : arr[n]) ans=(ans+i)%z;
        return ans;
    }
};
