class Solution {
public:

    int mod=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        int INV= 500;

        vector<vector<int>> dp(n+1,vector<int>(INV+1,0));

        dp[0][0]=1;

        unordered_map<int,int> hash;
        for(auto it:req) hash[it[0]+1]=it[1];

        for(int idx=1; idx<=n; idx++){
            int lim= 500;
            for(int inv=0;inv <= lim;inv++){

                for(int temp=max(0,inv-idx+1); temp<=inv; temp++){
                    dp[idx][inv]= ( dp[idx-1][temp] + dp[idx][inv]) % mod;
                } 

            }
            if(hash.count(idx)){
              for(int inv=0;inv<=INV;inv++){
                if(inv!= hash[idx]) dp[idx][inv]=0;
              }
            }
        }

        long long int ans=0;
        for(auto it:dp[n]){
            ans= (ans+it)%mod;
        }
        return ans%mod;
    }
};
