#define MOD ((long long)(1e9+7))
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<long long>> dp(n + 1, vector<long long>(401, 0));
        dp[0][0] = 1;
        long long int sum=0;
        unordered_map<int, int> mp;
        for (auto i : requirements)  mp[i[0] + 1] = i[1];
        for (int i = 1; i <= n; i++) {
            sum+=(i-1);
            for (int j = 0; j <= min(400*1LL,sum); j++) {
                for (int k = 0; k < i; k++){
                    if (j >= k)     dp[i][j] = ((dp[i][j]%MOD) + (dp[i - 1][j-k]%MOD)) % MOD;
                }
            }
            if (mp.find(i)!=mp.end()) {
                for (int j = 0; j <= min(400*1LL,sum); j++) {
                    if (j != mp[i])     dp[i][j] = 0;
                }
            }
        }
        long long ans = 0;
        for (auto i : dp[n])    ans = ((ans%MOD)+(i%MOD))% MOD;
        return ans;
    }
};
