mod = 1_000_000_007

class Solution:
    def numberOfPermutations(self, n: int, requi: List[List[int]]) -> int:    
        arr = {}
        maxi_ans = 400   
        dp = [[0] * (maxi_ans + 1) for _ in range(n + 1)]
        dp[0][0] = 1     
        for x in requi:
            arr[x[0] + 1] = x[1]

        for len in range(1, n + 1):
            for curr in range(maxi_ans + 1):
                for new in range(len):
                    temp = curr - new
                    if temp >= 0:
                        dp[len][curr] = (dp[len][curr] + dp[len - 1][temp]) % mod

            if len in arr:
                ans = arr[len]
                for curr in range(maxi_ans + 1):
                    if curr != ans:
                        dp[len][curr] = 0

        total = sum(dp[n]) % mod
        return total
