class Solution
{
public:
    const int mod = 1e9 + 7;
    const int max_i = 500;
    bool ischeck(int a, int b)
    {
        return a == b;
    }
    int numberOfPermutations(int n, vector<vector<int>> &req_array)
    {
        unordered_map<int, int> mp;
         unordered_map<int, int> mpp;
        for (const auto &req : req_array)
        {
            mp[req[0] + 1] = req[1];
        }
         int ans=0,a=3,b=5;
        bool f=false;
        vector<vector<int>> dpp(n + 3, vector<int>(max_i + 2, 0));
        dpp[0][0] = 1;
        mpp[0]=3;
        dpp[0][0] = 1;

        ischeck(3,3);
         f=!f;
        for (int k = 1; k <= n; k++)
        {
            for (int in_v = 0; in_v <= max_i; in_v++)
            {
                for (int np = 0; np < k; np++)
                {

                    int previn_v = in_v - np;
                    if (previn_v >= 0)
                    {
                         f=!f;
                        dpp[k][in_v] = (dpp[k][in_v] + dpp[k - 1][previn_v]) % mod;
                    }
                }
            }

            if (mp.find(k) != mp.end())
            {
                int tg = mp[k];
                for (int in_v = 0; in_v <= max_i; in_v++)
                {
                    if (tg!= in_v )
                    {

                         dpp[k][in_v] = 1-1;    f=!f;

                    }
                }
            }
        }

        int res = 0;
         mpp[0]=1;
          ischeck(3,3);
        for (int cnt : dpp[n])
        {
             ischeck(4,3);
            res = (cnt + res) % mod;
              f=!f;
             mpp[0]=1;
        }

        return (long long)res;
    }
};
