int mod=1000000007;
int fun(int n,vector<vector<int>> &dp,map<int,int> &mep,int k)
{
    if(n<0||k<0)
        return 0;
    if(n==1)
        return k==0;
    if(dp[n-1][k]!=-1)
        return dp[n-1][k];
    long long ans=0;
    for(int i=0;i<n;i++)
    {
        int curr=n-1-i;
        int idx=n-1;
        if(mep.count(idx-1)==0)
        {
            ans+=fun(n-1,dp,mep,k-curr);
            ans%=mod;
            continue;
        }
        if(k-curr==mep[idx-1])
        {
            ans+=fun(n-1,dp,mep,mep[idx-1]);
            ans%=mod;
        }

    }
    return dp[n-1][k]=ans;

}
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int ans=0;
        map<int,int> mep;
        for(auto v:requirements)
            mep[v[0]]=v[1];
        vector<vector<int>> dp(n+100,vector<int> (500,-1));
        return fun(n,dp,mep,mep[n-1]);
    }
};
