#define mod 1000000007

#define ll long long

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& arr) {
        ll cnt = 0;

        unordered_map<int, int> mp;

        for (auto &x: arr)  mp[x[0]+1]=x[1];

        vector<vector<ll>> vec(n+1,vector<ll>(400+1,0));

        vec[0][0] = 1;

        for (int i=1; i<=n; i++) {

            for (int j=0; j<=i*(i-1)/2 &&  j<=400; j++) {

                for (int k=0; k<i; k++) {
                    int prev = j-k;
                    if (prev >= 0)
                        vec[i][j] = (vec[i][j] + vec[i - 1][prev]) % mod;
                }
            }

            if (mp.find(i)!=mp.end()) {
                int temp = mp[i];
                for (int j = 0; j <= i * (i - 1) / 2 && j <= 400; j++) {
                    if (j != temp)
                        vec[i][j] = 0;
                }
            }
        }
        for (int i=0; i<=400; i++)  cnt = (cnt + vec[n][i]) % mod;


        int ans = (int) cnt;
        return ans;
    }
};
