#include <bits/stdc++.h>
using namespace std;
#define f(i,a,b) for(i=a;i<b;i++)
#define fi(i,a,b) for(i=a;i>=b;i--)
#define all(v) (v).begin(),(v).end()
#define vi(v) vector<int> v
#define vii(v) vector<ll> v
#define srt(v) sort(v, v + n)
#define no cout << "NO" << endl
#define yes cout << "YES" << endl
using ll = long long;
int i, j, z;
const int N = 500005;
ll mod = 1000000007;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<ll>> dp(305);
		f(i, 0, 303)dp[i] = vector<ll>(406);
		map<ll, ll> mp;
		dp[0][0] = 1;
		for(auto x : requirements){mp[x[0] + 1] = x[1];}
		f(i, 1, n + 1)
		{
			f(j, 0, 401)
			{
				f(z, 0, i)
				{
					if(j - z >= 0)
					{
						dp[i][j] += dp[i - 1][j - z];
						dp[i][j] %= mod;
					}
				}
			}
			if(mp.find(i) != mp.end())
			{
				f(j, 0, 401)
				{
					if(j == mp[i])continue;
					dp[i][j] = 0;
				}
			}
		}
	ll sans = 0;
	f(j, 0, 401)
	{
		sans += dp[n][j]; sans %= mod;
	}
	return sans;
	}
};
