class Solution {
public:
    int mod = 1e9+7;
    using ll = long long;

    int numberOfPermutations(int n, vector<vector<int>>& a) {

        map<long long,long long>m;
        for(auto it:a){
            m[1+it[0]]=it[1];
        }
        int ct=(n*(n-1))/2;
        ll ans=0;
        vector<vector<ll>>dp(n+1,vector<ll>(500,0));
        dp[0][0]=1;
        for(int i=0;i<=n;){
            if(i!=0){
            for(int j=0;j<500;){
                for(int k=0;k<i;){
                    if(j-k>=0)dp[i][j]=(dp[i-1][j-k]+dp[i][j])%mod;
                    k++;
                }
                j++;
            }
            if(m.find(i)!=m.end()){
               for(int j=0;j<500;j++)
                   if(m[i]!=j)
                       dp[i][j]=0;
            }
          }
            i++;
        }
        for(auto it:dp[n]){
             ans=(it+ans)%mod;
        }
        return ans;

    }
};
