class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        long long M = 1e9 + 7;
        int max_inv = 400;

        unordered_map<int, int> ump;

        for (vector<int> x : requirements) {
            ump[x[0] + 1] = x[1];
        }

        vector<vector<long long>> perm(n + 1, vector<long long>(max_inv + 1));
        perm[0][0] = 1;

        for (int l = 1; l <= n; l++) {
            for (int inv = 0; inv <= max_inv; inv++) {
                for (int nw = 0; nw < l; nw++) {
                    int pI = inv - nw;
                    if (pI >= 0) {
                        perm[l][inv] = (perm[l][inv] + perm[l - 1][pI]) % M;
                    }
                }
            }

            if (ump.find(l) != ump.end()) {
                for (int i = 0; i <= max_inv; i++) {
                    if (i != ump[l]) {
                        perm[l][i] = 0;
                    }
                }
            }
        }

        long long result = 0;

        for (long r : perm[n]) {
            result = (result + r) % M;
        }

        return result;
    }
};
