int dp[305][90005];
int val[305];
class Solution {
public:

    const int mod=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        for(int i = 0;i<n;i++){
            for(int j = 0;j<=n*n;j++){
               dp[i][j]=0;
            }
           val[i]=-1;
        }
        for(auto it:requirements){
            if(val[it[0]]!=-1)return 0;
            val[it[0]]=it[1];
        }
        dp[0][0]=1;
        for(int i = 1;i<=n*n;i++)dp[0][i]=1;
        if(val[0]!=-1&&val[0]!=0)return 0;
        for(int i = 1;i<n;i++){
            for(int j =0;j<=n*n;j++){
                if(val[i]!=j&&val[i]!=-1)continue;
                if(j-i-1>=0)
                dp[i][j]=dp[i-1][j]-dp[i-1][j-i-1];
                else{
                dp[i][j]=dp[i-1][j];
                }
                dp[i][j]=(dp[i][j]%mod+mod)%mod;
            }
          //  for(int j = 0;j<=n*n;j++)printf("%d ",dp[i][j]);
          //  printf("\n");
            for(int j = 1;j<=n*n;j++){
                dp[i][j]+=dp[i][j-1];
                dp[i][j]%=mod;
            }
        }
    //    printf("%d %d\n",n-1,n*n);
        return dp[n-1][n*n];
    }
};
