mod = 10 ** 9 + 7
class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        d = {end + 1: cnt for end, cnt in requirements}
        dp = [[0] * 401 for _ in range(n + 1)]
        dp[0][0] = 1
        for i in range(1, n + 1):
            for j in range(401):
                for k in range(i):
                    cnt = j - k
                    if cnt >= 0:
                        dp[i][j] = (dp[i][j] + dp[i - 1][cnt]) % mod
            if i in d:
                for j in range(401):
                    if j != d[i]:
                        dp[i][j] = 0
        return sum(dp[-1]) % mod
