class Solution {
public:
    int v[301][401];
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<int, int> mp;
        int mod=1e9+7;
    for (auto x : requirements)
    {
        mp[x[0] + 1] = x[1];
    }
    memset(v, 0, sizeof(v));
    v[0][0] = 1;
    for (int i = 1; i <= n; i++)
    {
        for (int j = 0; j <401; j++)
        {
            v[i][j] = 0;
            for (int k = 0; k < i; k++)
            {
                if (j-k >= 0)
                {
                    v[i][j] = (v[i - 1][j-k]+v[i][j]) % mod;
                }
            }
        }
        if (mp.count(i)!=0)
        {
            for (int j = 0; j <401; j++)
            {
                if (j != mp[i])
                {
                    v[i][j] = 0;
                }
            }
        }
    }
        // for(int i=0;i<10;i++){
        //     cout<<v[n][i]<<endl;
        // }

    int ans = 0;
    for (int i = 0; i <401; i++)
    {
        ans = (ans + v[n][i]) % mod;
    }

    return ans;
    }
};
