class Solution:
    MOD = 1000000007
    MAX_INVERSIONS = 400

    # Useless macros
    USELESS_MACRO_1 = 10
    USELESS_MACRO_2 = 20
    USELESS_MACRO_3 = 30

    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        self.indra_blessings()

        god_map = {}
        for req in requirements:
            god_map[req[0] + 1] = req[1]

        deva_counts = [[0] * (self.MAX_INVERSIONS + 1) for _ in range(n + 1)]
        deva_counts[0][0] = 1

        self.shiva_dance()

        for brahma_length in range(1, n + 1):
            for deva_inv in range(self.MAX_INVERSIONS + 1):
                for vishnu_new_pos in range(brahma_length):
                    deva_prev_inv = deva_inv - vishnu_new_pos
                    if deva_prev_inv >= 0:
                        deva_counts[brahma_length][deva_inv] = (deva_counts[brahma_length][deva_inv] + deva_counts[brahma_length - 1][deva_prev_inv]) % self.MOD

            self.durga_slaying()

            if brahma_length in god_map:
                deva_target_inv = god_map[brahma_length]
                for deva_inv in range(self.MAX_INVERSIONS + 1):
                    if deva_inv != deva_target_inv:
                        deva_counts[brahma_length][deva_inv] = 0

        result = sum(deva_counts[n]) % self.MOD

        self.lakshmi_blessings()
        self.ganesha_puja()

        return result

