class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        #define ll long long
        const ll mod = 1e9 + 7;
        ll dp[n+1][500];
        memset(dp,0,sizeof(dp));
        dp[0][0] = 1;
        sort(req.begin(),req.end());
        int ind = 0;
        if(req[0][0] == 0){
            ind++;
            if(req[0][1] != 0)return 0;
        }

        for(int i=1;i<n;i++){
            for(int j=0;j<=405;j++){
                dp[i][j] =0;
                for(int k=0;k<=i;k++){
                    if(k>j)break;
                    dp[i][j] = (dp[i][j] +  1LL*dp[i-1][j-k])%mod;
                    dp[i][j] %= mod;
                }
            // cout<<i<<" "<<j<<
            }
            if(i!= n-1 && req[ind][0] == i){
                map<int,int> mark;
                while(req[ind][0] == i){
                    mark[req[ind][1]] = 1;
                    ind++;
                }
                for(int j=0;j<=400;j++){
                    if(mark.find(j) == mark.end())dp[i][j] =0 ;
                }
                // ind++;
            }
        }
        ll ans =0 ;
        for(int i=ind;i<req.size();i++){
            ans += dp[n-1][req[i][1]];
            ans %= mod;
        }

        return ans;



    }
};
