class Solution:
    def numberOfPermutations(self, n, reqs):
        MOD = 1000000007
        MAX_INV = 400

        r = {}
        for req in reqs:
            r[req[0] + 1] = req[1]

        pc = [[0] * (MAX_INV + 1) for _ in range(n + 1)]
        pc[0][0] = 1

        for length in range(1, n + 1):
            for inv in range(MAX_INV + 1):
                for pos in range(length):
                    prev_inv = inv - pos
                    if prev_inv >= 0:
                        pc[length][inv] = (pc[length][inv] + pc[length - 1][prev_inv]) % MOD

            if length in r:
                target_inv = r[length]
                for inv in range(MAX_INV + 1):
                    if inv != target_inv:
                        pc[length][inv] = 0

        res = sum(pc[n]) % MOD
        return res
