using ll=long long;
const ll mod=1e9+7;

ll dp[303][404];

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& V) {
        vector<vector<int>> req(n+1);
        for (auto &p: V) {
            int idx=p[0]+1;
            int r=p[1];
            req[idx].push_back(r);
        }


        memset(dp, 0, sizeof(dp));
        dp[0][0]=1;

        for (int i=1; i<=n; i++) {
            int iv=min(400, i*(i-1)/2);
            for (int j=0; j<i; j++) {
                for (int v=(req[i].size() ? req[i][0] : 0); v<=(req[i].size() ? req[i][0] : iv); v++) {
                    if (v-j>=0) {
                        dp[i][v] += dp[i-1][v-j];
                        dp[i][v] %= mod;
                    }
                }
            }
        }


        ll res=0;
        for (int j=0; j<=min(400, n*(n-1)/2); j++) {
            // cout<<j<<": "<<dp[n][j]<<endl;
            res += dp[n][j];
            res %= mod;
        }
        return res;

    }
};
