const MOD = 1000000007;
const MAX_INVERSIONS = 400;

var numberOfPermutations = function(n, requirements) {
    let reqMap = new Map();
    for (let req of requirements) {
        reqMap.set(req[0] + 1, req[1]);
    }

    let permCounts = new Array(n + 1).fill(null).map(() => new Array(MAX_INVERSIONS + 1).fill(0));
    permCounts[0][0] = 1;

    for (let length = 1; length <= n; length++) {
        for (let inv = 0; inv <= MAX_INVERSIONS; inv++) {
            for (let newPos = 0; newPos < length; newPos++) {
                let prevInv = inv - newPos;
                if (prevInv >= 0) {
                    permCounts[length][inv] = (permCounts[length][inv] + permCounts[length - 1][prevInv]) % MOD;
                }
            }
        }

        if (reqMap.has(length)) {
            let targetInv = reqMap.get(length);
            for (let inv = 0; inv <= MAX_INVERSIONS; inv++) {
                if (inv !== targetInv) {
                    permCounts[length][inv] = 0;
                }
            }
        }
    }

    let result = 0;
    for (let count of permCounts[n]) {
        result = (result + count) % MOD;
    }

    return result;
};
