class Solution {
    private static final int MOD = 1000000007;
    public int numberOfPermutations(int n, int[][] requirements) {
   int maxInversions = n * (n - 1) / 2;
        int[][] dp = new int[n + 1][maxInversions + 1];
        dp[0][0] = 1;

        for (int len = 1; len <= n; len++) {
            int[] prefixSum = new int[maxInversions + 2];
            for (int inv = 0; inv <= maxInversions; inv++) {
                prefixSum[inv + 1] = (prefixSum[inv] + dp[len - 1][inv]) % MOD;
            }
            for (int inv = 0; inv <= maxInversions; inv++) {
                dp[len][inv] = prefixSum[inv + 1];
                if (inv - len >= 0) {
                    dp[len][inv] = (dp[len][inv] - prefixSum[inv - len + 1] + MOD) % MOD;
                }
            }
        }

        Map<Integer, Integer> reqMap = new HashMap<>();
        for (int[] req : requirements) {
            reqMap.put(req[0], req[1]);
        }

        int[][] dpValid = new int[n + 1][maxInversions + 1];
        dpValid[0][0] = 1;

        for (int len = 1; len <= n; len++) {
            int[] prefixSum = new int[maxInversions + 2];
            for (int inv = 0; inv <= maxInversions; inv++) {
                prefixSum[inv + 1] = (prefixSum[inv] + dpValid[len - 1][inv]) % MOD;
            }
            for (int inv = 0; inv <= maxInversions; inv++) {
                dpValid[len][inv] = prefixSum[inv + 1];
                if (inv - len >= 0) {
                    dpValid[len][inv] = (dpValid[len][inv] - prefixSum[inv - len + 1] + MOD) % MOD;
                }
                if (reqMap.containsKey(len - 1) && reqMap.get(len - 1) != inv) {
                    dpValid[len][inv] = 0;
                }
            }
        }

        int result = 0;
        for (int inv = 0; inv <= maxInversions; inv++) {
            result = (result + dpValid[n][inv]) % MOD;
        }

        return result;
    }



}
