class Solution {
public:
    int dp[300][401],N,ar[300];
    const int mod=1e9+7;
    int ok(int i,int j)
    {
        if(i<0||j<0)
        {
            return j==0;
        }
        else if(ar[i]>=0&&j!=ar[i])
        {
            return 0;
        }
        else if(dp[i][j]!=-1)
        {
            return dp[i][j];
        }
        else
        {
            int ans=0;
            for(int l=1;l<=i+1;l++)
            {
                ans=(ans+ok(i-1,j-(i+1-l)))%mod;
            }
            return dp[i][j]=ans;
        }
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        N=n;
        for(int i=0;i<n;i++)
        {
            ar[i]=-1;
        }
        for(auto a:requirements)
        {
            ar[a[0]]=a[1];
        }
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<=ar[n-1];j++)
            {
                dp[i][j]=-1;
            }
        }
        return ok(n-1,ar[n-1]);
    }
};
