class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        req = defaultdict(int)
        mod = 10**9 + 7
        for e, c in requirements:
            req[e] = c

        @cache
        def rec(le, numInv):
            if le == 0:
                if numInv == 0:
                    return 1
                return 0
            if numInv < 0:
                return 0
            if le - 1 in req:
                if numInv != req[le - 1]:
                    return 0
            ans = 0
            for i in range(le):
                ans += rec(le - 1, numInv - le + 1 + i)
            return ans % mod

        return rec(n, req[n - 1])
