class Solution {
    const int mod = 1e9 + 7;
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int i, j, k, dp[300][405]{{1}};
        unordered_map<int, int> req;
        for (auto &r: requirements) req[r[0]] = r[1];
        if (req[0]) return 0;
        for (i = 1; i < n; i++) {
            for (j = req[n - 1]; j >= 0; j--) {
                for (k = min(i, j); k >= 0; k--) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % mod;
                }
            }
            if (req.count(i)) {
                for (j = req[n - 1]; j >= 0; j--) {
                    if (j != req[i]) dp[i][j] = 0;
                }
            }
        }
        return dp[n - 1][req[n - 1]];
    }
};
