const int MOD = 1e9 + 7;
int dp[304][405];
int val[304];

class Solution {
   public:
    int solve(int p, int inv, int& n) {
        if (inv > 400) return 0;
        if (p >= n) {
            return 1;
        }
        int& rs = dp[p][inv];
        if (rs != -1) return rs;
        rs=0;
        for (int j = 0; j <= p; j++) {
            if (inv + j == val[p] || val[p] == -1) {
                rs = (rs + solve(p + 1, inv + j, n)) % MOD;
            }
        }
        return rs;
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        memset(dp, -1, sizeof(dp));
        memset(val, -1, sizeof(val));
        for (auto& u : requirements) {
            val[u[0]] = u[1];
        }
        return solve(0, 0, n);
    }
};
