class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD = 10**9+7
        dp = [0]*401
        req = [-1]*n
        for x, y in requirements:
            req[x] = y
        dp[req[n-1]] = 1
        for i in range(n-2, -1, -1):
            pref = [0]
            for a in dp:
                pref.append(pref[-1]+a)
            ndp = [(pref[min(j+i+2, 401)]-pref[j])%MOD for j in range(401)]
            if req[i] != -1:
                for j in range(401):
                    if j != req[i]:
                        ndp[j] = 0
            dp = ndp
        return dp[0]%MOD
