class Solution {
public:
    const int lim = 400; 
    const int mod = 1e9 + 7; 
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int dp[n + 1][lim + 1]; 
        int predp[n + 1][lim + 1]; 
        for(int i = 0; i <= n; ++i)
        {
            for(int j = 0; j <= lim; ++j)
            {
                dp[i][j] = 0; 
                predp[i][j] = 0; 
            }
        }
        dp[0][0] = 1; 
        for(int j = 1; j <= lim; ++j)
            dp[0][j] = 0; 
        predp[0][0] = dp[0][0]; 
        for(int i = 1; i <= lim; ++i) 
            predp[0][i] = predp[0][i - 1] + dp[0][i]; 

        sort(requirements.begin(),requirements.end()); 
        int it = 0; 
        for(int i = 1; i <= n; i++)
        {
            for(int j = 0; j <= lim; j++)
            {
                if(j-i>= 0)
                    dp[i][j] = (predp[i-1][j] - predp[i-1][j-i])%mod;  
                else 
                    dp[i][j] = predp[i-1][j]; 
            }
            if(it != requirements.size() && 1 + requirements[it][0] == i)
            {
                for(int j = 0; j <= lim; ++j)
                {
                    if(j != requirements[it][1]) 
                        dp[i][j] = 0; 
                }
                ++it;
            }
            predp[i][0] = dp[i][0]; 
            for(int j = 1; j <= lim; ++j)
            {
                predp[i][j] = (dp[i][j] + predp[i][j-1])%mod; 
            }
        }
        return (predp[n][lim] + mod)%mod; 

    }
};
