#define ll long long

const ll mod = 1e9 + 7;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        ll dp[n + 1][401];
        memset(dp, 0, sizeof(dp));

        dp[0][0] = 1;

        unordered_map<int, int> mp;

        for(auto it: requirements){
            mp[it[0]] = it[1];
        }

        for(int i = 1; i <= n; ++i) {
            if(mp.count(i-1)) {
                for(int j = mp[i-1]; j >= max(mp[i-1] - i + 1, 0); --j) { 
                    dp[i][mp[i-1]] += dp[i-1][j];
                    dp[i][mp[i-1]] %= mod;
                }
            } else {
                for(int inv = 0; inv < 401; ++inv) {
                    for(int j = inv; j >= max(inv - i + 1, 0); --j){
                        dp[i][inv] += dp[i-1][j];
                        dp[i][inv] %= mod;
                    }
                }
            }
        }

        ll ans = 0;

        for(int inv = 0; inv < 401; ++inv) {
            ans += dp[n][inv];
            ans %= mod;
        }

        return (int)ans;
    }
};
