const M: i32 = 1000000007;

impl Solution {
    pub fn number_of_permutations(n: i32, requirements: Vec<Vec<i32>>) -> i32 {
        let mut req = vec![None; n as usize];
        for r in &requirements {
            let end = r[0];
            let cnt = r[1];
            req[end as usize] = Some(cnt);
        }
        if let Some(x) = req[0] {
            if x != 0 {
                return 0;
            }
        }


        let mut dp = vec![vec![0; 401]; (n + 1) as usize];
        dp[1][0] = 1;
        for i in 2..=(n as usize) {
            for j in 0..=400 {
                if let Some(r) = req[i - 1] {
                    if j as i32 != r {
                        continue;
                    }
                }
                for k in 0..i {
                    if j < k { break; }
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % M;
                }
            }
        }
        dp[n as usize][req[n as usize - 1].unwrap() as usize]
    }
}
