    int mod = 1000000007;

    int dp[405][405];



    int rec(int i,int k,vector<int> &v){


        if(v[i-1]!=-1 and v[i-1]!=k){
            return 0;
        }

        if(i==1){
            return (k==0);
        }


        if(dp[i][k]!=-1){
            return dp[i][k];
        }

        int ans=0;
        for(int j=0;j<=k;++j){
            if(j<=i-1){
                ans = (ans + rec(i-1,k-j,v))%mod;
            }
        }    

        return dp[i][k]=ans;
    }

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> v(n,-1);
        memset(dp,-1,sizeof(dp));
        for(auto i:requirements){
            v[i[0]]=i[1];
        }
        return rec(n,v[n-1],v);

    }
