#define ll long long
#define REP(i,n)        FOR(i,0,n)
#define SORT(v)         sort((v).begin(),(v).end())
#define FOR(i,a,b)      for(ll i=(a);i<(b);i++)

class Solution {
public:
    ll denom = 1e9+7;
    ll n;

    ll count(ll idx, ll sum, vector<ll>& req, vector<vector<ll>>& dp) {
        if (idx == n) return 1;
        if (sum > 400) return 0;
        if (dp[idx][sum] != -1) return dp[idx][sum];
        if (req[idx] < sum) return 0;

        ll res = 0;
        REP(i, idx+1) {
            if (req[idx] != INT_MAX && req[idx] != i+sum) continue;
            res += count(idx+1, i+sum, req, dp);
            res %= denom;
        }
        return dp[idx][sum] = res;
    }

    int numberOfPermutations(int n, vector<vector<int>>& r) {
        this->n = n;
        vector<ll> req(n, INT_MAX); for (auto& v : r) req[v[0]] = v[1];
        vector<vector<ll>> dp(n, vector<ll>(401, -1));
        return count(0, 0, req, dp);
    }
};
