const int MOD = 1e9 + 7;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<int>> dp(n + 1, vector<int>(400 + 1, 0));
        dp[0][0] = 1;

        for (int i = 1; i <= n; ++i) {
            for (int j = 0; j <= 400; ++j) {
                for (int k = 0; k < i; ++k) {
                    if (j >= k) {
                        dp[i][j] = (dp[i][j] + dp[i-1][j-k]) % MOD;
                    }
                }
            }
        }

        vector<int> ind(n, -1);
        for (const auto& req : requirements) {
            ind[req[0]] = req[1];
        }

        vector<vector<int>> dp2(n + 1, vector<int>(401, 0));
        dp2[0][0] = 1;

        for (int i = 1; i <= n; ++i) {
            for (int j = 0; j <= 400; ++j) {
                for (int k = 0; k < i; ++k) {
                    if (j >= k) {
                        dp2[i][j] = (dp2[i][j] + dp2[i-1][j-k]) % MOD;
                    }
                }
            }
            if (ind[i-1] != -1) {
                int t = ind[i-1];
                for (int j = 0; j <= 400; ++j) {
                    if (j != t) {
                        dp2[i][j] = 0;
                    }
                }
            }
        }

        int total = dp2[n][0];
        for (int j = 1; j <= 400; ++j) {
            total = (total + dp2[n][j]) % MOD;
        }

        return total;
    }
};
