typedef long long ll;
const ll mod = 1e9 + 7;
class Solution {
public:
    typedef long long ll;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int m = requirements.size();
        vector<vector<ll> > dp(n + 1, vector<ll>(401, 0));
        vector<ll> cnt(n  + 1, -1);
        for (int i = 0; i < m; i ++){
            cnt[requirements[i][0] + 1] = requirements[i][1];
        }
        dp[0][0] = 1;
        for (int i = 1; i <= n; i ++){
            if (cnt[i] == -1){
                for (ll j = 0; j <= 400; j ++){
                    for (ll k = 0; k <= min(j, (ll)i - 1); k ++){
                        dp[i][j] += dp[i - 1][j - k];
                        dp[i][j] %= mod;
                    }
                }
            }
            else{
                for (ll k = 0; k <= min(cnt[i], (ll)i - 1); k ++){
                    dp[i][cnt[i]] += dp[i - 1][cnt[i] - k];
                    dp[i][cnt[i]] %= mod;
                }
            }
        }
        ll ans = 0;
        for (ll i = 0; i <= 400; i ++)
            ans += dp[n][i];
        return ans % mod;
    }
};
