class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        const int mod=1e9+7;
        vector<int>v(n+1,-1);
        sort(requirements.begin(),requirements.end());
        for(auto it:requirements)
            v[it[0]+1]=it[1];
        int dp[n+1][402];
        int pre[402];
        ::memset(dp,0,sizeof dp);
        memset(pre,0,sizeof pre);
        dp[1][0]=1;
        for (int i = 0; i <=400 ; ++i) {
            pre[i]=1;
        }
        if(v[1]>0)
        {
            return 0;
        }
        for (int i = 2; i <=n ; ++i) {
            if(v[i]==-1)
            {
                for (int j = 0; j <=400; ++j) {
                    if(j<i)
                        dp[i][j]=pre[j],dp[i][j]%=mod;
                    else
                        dp[i][j]=pre[j]-pre[j-i],dp[i][j]%=mod,dp[i][j]+=mod,dp[i][j]%=mod;
                }
            }
            else
            {
                if(v[i]<i)
                    dp[i][v[i]]=pre[v[i]];
                else
                    dp[i][v[i]]=pre[v[i]]-pre[v[i]-i];
                dp[i][v[i]]%=mod;
                dp[i][v[i]]+=mod;
                dp[i][v[i]]%=mod;
            }
            pre[0]=dp[i][0];
            for (int j = 1; j <=400 ; ++j) {
                pre[j]=pre[j-1]+dp[i][j];
                pre[j]%=mod;
            }
        }
        return dp[n][requirements.back()[1]];
    }

};
