class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        long long mod = 1000000007;

        int m = requirements.size();

        sort(requirements.begin(), requirements.end());

        vector<vector<long long>> memo(n+1, vector<long long>(401, 0));

        memo[0][0] = 1;

        int j = 0;

        if (requirements[0][0] == 0) {
            if (requirements[0][1] != 0) {
                return 0;
            } else {
                j = 1;
            }
        }

        for (int i = 1; i < n; ++i) {
            if (j < m && requirements[j][0] == i) {
                for (int k = 0; k < 401; ++k) {
                    if (requirements[j][1] <= k + i && requirements[j][1] >= k) {
                        memo[i][requirements[j][1]] += memo[i-1][k];
                        memo[i][requirements[j][1]] %= mod;
                    }
                }
                j++;
            } else {
                for (int k = 0; k < 401; ++k) {
                    for (int l = 0; l <= i; ++l) {
                        if (k + l < 401) {
                            memo[i][k+l] += memo[i-1][k];
                            memo[i][k+l] %= mod;
                        }
                    }
                }
            }
        }

        long long sol = 0;

        for (int k = 0; k < 401; ++k) {
            // cout<<memo[n-1][k]<<endl;
            sol += memo[n-1][k];
            sol %= mod;
        }

        // for (int i = 0; i < n; ++i) {
        //     for (int k = 0; k < 401; ++k) {
        //         cout<<memo[i][k]<<" ";
        //     }
        //     cout<<endl;
        // }

        return sol;
    }
};
