class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int MAXX = min(402, n * n + 10); 
        VVL dp(n, VL(MAXX, 0));
        LL i, j, k, MODD = 1000000007LL, last = 0, ans;
        VL v(n, -1);
        for (auto p : requirements) v[p[0]] = p[1];


        dp[0][0] = 1;
        FOR(i, n) {
            if (v[i] != -1) {
                FOR(j, MAXX) {
                    if (j != v[i]) dp[i][j] = 0;
                }
            }
            if (i < n - 1) {
                FOR(j, MAXX) {
                    if (dp[i][j] == 0) continue;
                    for (k = 0; (k <= i + 1) && (j + k < MAXX); k++) {
                        dp[i + 1][j + k] = (dp[i + 1][j + k] + dp[i][j]) % MODD;
                    }
                }
            }
            else {
                ans = 0;
                FOR(j, MAXX) {
                    ans = (ans + dp[i][j]) % MODD;
                }
            }
        }
        return ans;
    }
};
