#define ll long long int

class Solution {
public:
    ll find(vector<ll>& v,ll ind,ll cur,ll target,ll **dp){
        if(cur>target){
            return 0;
        }
        if(cur==target){
            return 1;
        }
        if(ind==v.size()){
            return 0;
        }
        if(dp[ind][cur]!=-1){
            return dp[ind][cur];
        }
        ll ans=0;
        for(ll i=0;i<=v[ind];i++){
            ans=(ans+find(v,ind+1,cur+i,target,dp))%1000000007;
        }
        return dp[ind][cur]= ans;
    }
    int numberOfPermutations(int n, vector<vector<int>>& queries) {
          sort(queries.begin(),queries.end());
          ll prevInd=0;
          ll minus=0;
          ll cur=0;
          ll ans=1;
          for(ll i=0;i<queries.size();i++){
              ll ind=queries[i][0];
              ll tar=queries[i][1];
              ll temp=tar;
              tar=tar-minus;
              vector<ll> v;
              for(ll j=prevInd;j<=ind;j++){
                  v.push_back(cur);
                  cur++;
              }
              ll x=v.size();
              ll **dp=new ll*[x];
              for(ll j=0;j<x;j++){
                  dp[j]=new ll[tar+1];
              }
              for(ll j=0;j<x;j++){
                  for(ll k=0;k<=tar;k++){
                      dp[j][k]=-1;
                  }
              }
              ll see=find(v,0,0,tar,dp);
              ans=(ans*see)%1000000007;
              prevInd=ind+1;
              minus=temp;
          }
        return ans;
    }
};
