class Solution {
public:
    int MOD = 1e9 + 7;

    int numberOfPermutations(int n, vector<vector<int>>& a) {
        sort(a.begin(), a.end());

        vector<vector<int>> dp(n + 1, vector<int>(401, 0));

        dp[0][0] = 1;

        for (int i=1; i<=n; i++) 
        {
            vector<vector<int>> new_dp(i+1, vector<int>(401, 0));

            for (int j=0; j<=400; j++) 
            {
                for (int k=0; k<i; k++) 
                {
                    if (j-k >= 0) 
                        new_dp[i][j] = (new_dp[i][j] + dp[i-1][j-k]) % MOD;
                }
            }

            dp = new_dp;
        }

        vector<vector<int>> dp2(n + 1, vector<int>(401, 0));
        dp2[0][0] = 1;

        int ind = 0;
        for (int i=1; i<=n; i++) 
        {
            for (int j=0; j<=400; j++) 
            {
                for (int k=0; k<i; k++) 
                {
                    if (j-k >= 0)
                        dp2[i][j] = (dp2[i][j] + dp2[i-1][j-k]) % MOD;
                }
            }

            if (ind < a.size() && a[ind][0] == i-1) 
            {
                for (int j=0; j<=400; j++) 
                {
                    if (j != a[ind][1])
                        dp2[i][j] = 0;
                }

                ind++;
            }
        }

        int result = 0;
        for (int i = 0; i <= 400; i++)
            result = (result + dp2[n][i]) % MOD;

        return result;
    }
};
