const long long MOD = 1e9 + 7;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& a) {
		long long dp[n+1][404];
		vector<int>pos(n,-1);
		for(auto& i : a) pos[i[0]]=i[1];
		dp[0][0]=1;
		for(int i = 1;i <= n;i++) {
			for(int j = 0;j <= pos[n-1];j++) {
				dp[i][j] = (j ? dp[i][j-1] : 0) + dp[i-1][j] + (j >= i ? -dp[i-1][j-i] : 0);
				dp[i][j] = (dp[i][j] % MOD + MOD) % MOD;
			}
			if(~pos[i-1]) {
				for(int j = 0;j < 404;j++) {
					if(j == pos[i-1]) continue;
					dp[i][j]=0;
				}
			}
		}
		if(pos[n-1]) {
			dp[n][pos[n-1]] -= dp[n][pos[n-1]-1];
		}
        return (dp[n][pos[n-1]] % MOD + MOD) % MOD;
    }
};
