class Solution {
public:
    long long dp[301][401];
    long long mod = 1e9 + 7;
    long long f(int n, vector<int> &arr, int cnt){
        // cout<<n<<" "<<cnt<<endl;
        if(n < 0) return cnt == 0;
        if(n*(n+1)/2 < arr[n]) return 0;
        if(cnt < 0) return 0;
        if(arr[n] != -1 && arr[n] != cnt) return 0;

        if(dp[n][cnt] != -1) return dp[n][cnt];


        long long ans = 0;
        for(int i=0; i<=n; ++i){
            ans += f(n-1, arr,cnt - n + i);
            ans %= mod;
        }

        return dp[n][cnt] = ans;
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> arr(n, -1);
        for(auto &it : requirements){
            int i = it[0], c = it[1];
            arr[i] = c;
        }
        memset(dp, -1, sizeof(dp));

        // for(auto &it :  arr) cout<<it<<" ";
        // cout<<endl;

        return (int)f(n-1, arr, arr[n-1]);
    }
};
