class Solution
{
public:
    const int mod = 1e9 + 7;
    const int cntInv = 400;
    int numberOfPermutations(int n, vector<vector<int>> &requirements)
    {
        vector<vector<int>> dp(n + 1, vector<int>(cntInv + 1, 0));
        dp[0][0] = 1;

        vector<int> nDp(n, -1);
        for (auto &i : requirements)
        {
            nDp[i[0]] = i[1];
        }

        for (int i = 1; i <= n; i++)
        {
            for (int j = 0; j <= cntInv; j++)
            {
                for (int k = 0; k < i; k++)
                {
                    if(j < k)
                    {
                        continue;
                    }
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % mod;
                }
            }

            if (nDp[i - 1] != -1)
            {
                int cntInversions = nDp[i - 1];
                for (int j = 0; j <= cntInv; j++)
                {
                    if (j != cntInversions)
                    {
                        dp[i][j] = 0;
                    }
                }
            }
        }

        return dp[n][nDp[n - 1]];
    }
};
