int dp[305][405];
class Solution {
public:
    int mod = 1e9+7;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {


        for(int i=0; i<305; i++){
            for(int j=0; j<405; j++){
                dp[i][j] = 0;
            }
        }

        map<int, int> mp;
        for(int i=0; i<requirements.size(); i++){
            mp[requirements[i][0]] = requirements[i][1];
        }

        for(int i=0; i<n; i++){

            if(i==0){
                if(mp[i]!=0) return 0;
                else dp[i][0] =1 ;

                continue;
            }
            else{

                if(mp.find(i)!=mp.end()){
                    for(int j=mp[i]; j<=mp[i]; j++){
                        for(int k=0; k<=i and j-k>=0; k++){
                            if(j-k>=0) dp[i][j]+=dp[i-1][j-k];
                            dp[i][j]%=mod;
                        }
                    }
                }
                else{
                    for(int j=0; j<405; j++){
                        for(int k=0; k<=i and j-k>=0; k++){
                            if(j-k>=0) dp[i][j]+=dp[i-1][j-k];
                            dp[i][j]%=mod;
                        }
                    }
                }

            }

        }

        int count = 0;
        for(int i=0; i<405; i++){
            count+=dp[n-1][i];
            count%=mod;
        }

        return count;
    }
};
