#define ll long long
#define mod 1000000007
class Solution {
public:
    int dp[305][410];
     ll f(int idx,int inv,map<int,int>&mp, int n){
         if(idx == n){
             return 1;   
         }

         if(inv >= 405) return 0;

         ll ans = 0;
         // if(dp.find({idx,inv})!=dp.end()) return dp[{idx,inv}];
         if(dp[idx][inv]!=-1) return dp[idx][inv];
         if(mp.find(idx)!=mp.end()){
             for(int i=0;i<=idx; i++){
                 int newInv = inv+(idx-i);
                 if(newInv == mp[idx]){
                     ans+= f(idx+1,newInv,mp,n);
                     ans%=mod;
                 }
             }
         }
         else{
             for(int i=0;i<=idx; i++){
                 ans+= f(idx+1,inv+(idx-i),mp,n);
                 ans%=mod;
             }
         }
         return dp[idx][inv] = ans;
     }

    int numberOfPermutations(int n, vector<vector<int>>& req) {
        memset(dp,-1,sizeof(dp));
        map<int,int>mp;
        for(auto x: req){
            mp[x[0]] = x[1];
        }
        return f(0,0,mp,n);

    }
};
